import type { Config } from "@measured/puck";
import * as AIPlugin from "@puckeditor/plugin-ai";



console.log("AI key present:", !!process.env.OPENAI_API_KEY);

type Props = {
  HeadingBlock: { title: string };
};

const aiPlugin =
  (AIPlugin as any).default ?? AIPlugin;

const config = {
  plugins: [
    aiPlugin({
      openai: {
        apiKey: process.env.OPENAI_API_KEY!,
      },
    }),
  ],

  components: {
    HeadingBlock: {
      fields: {
        title: { type: "text" },
      },
      defaultProps: {
        title: "Heading",
      },
      render: ({ title }: { title: string }) => (
        <div style={{ padding: 64 }}>
          <h1>{title}</h1>
        </div>
      ),
    },
  },
} as unknown as Config<Props>;

export default config;

